var converter = require('../converter'),
    chai = require('chai'),
    expect = chai.expect;

describe('converter', function () {
  it('throws on wrong parameters', function () {
    expect(converter.bind(converter)).to.throw(/one argument/);
    expect(converter.bind(converter, "foo", "bar")).to.throw("Expected to see one argument");
  });

  it('throws on parameter of a wrong type', function () {
    expect(converter.bind(converter, 1)).to.throw("Expected to see a string");
    expect(converter.bind(converter, {})).to.throw("Expected to see a string");
  });

  it('just works', function () {
    expect(converter('-9223372036854773696')).to.equal('9223372036854777920');
    expect(converter('-9223372036854708224')).to.equal('9223372036854843392');
    expect(converter('-9223372036750900225')).to.equal('9223372036958651391');
    expect(converter('-9223372036853659617')).to.equal('9223372036855891999');
    expect(converter('-9223372036852611041')).to.equal('9223372036856940575');
    expect(converter('-9223372036784454657')).to.equal('9223372036925096959');
    expect(converter('-9223372036854740928')).to.equal('9223372036854810688');
    expect(converter('-9223372036681595905')).to.equal('9223372037027955711');
    expect(converter('-9223372036785503233')).to.equal('9223372036924048383');
    expect(converter('-9223372036854773760')).to.equal('9223372036854777856');
    expect(converter('-9223372036854775744')).to.equal('9223372036854775872');
    expect(converter('-9223372036754112445')).to.equal('9223372036955439171');
  });
});

